-- ALPHA CMS 11.0 DB



CREATE TABLE `alpha_common` (
  `id` int(3) unsigned NOT NULL AUTO_INCREMENT,
  `site_title` varchar(60) NOT NULL,
  `site_description` varchar(170) DEFAULT NULL,
  `site_keywords` varchar(255) DEFAULT NULL,
  `company_name` varchar(100) DEFAULT NULL,
  `company_site` varchar(255) DEFAULT NULL,
  `footer_info` varchar(255) DEFAULT NULL,
  `binded_route` varchar(255) NOT NULL,
  `lang_id` int(3) unsigned NOT NULL,
  `is_protected` tinyint(1) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  FULLTEXT KEY `site_title` (`site_title`),
  FULLTEXT KEY `site_description` (`site_description`),
  FULLTEXT KEY `site_keywords` (`site_keywords`),
  FULLTEXT KEY `footer_info` (`footer_info`),
  FULLTEXT KEY `binded_route` (`binded_route`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;



CREATE TABLE `alpha_content` (
  `id` int(5) unsigned NOT NULL AUTO_INCREMENT,
  `page` varchar(255) NOT NULL,
  `content` longtext,
  `keywords` varchar(255) DEFAULT NULL,
  `lang_id` int(3) unsigned NOT NULL,
  `is_protected` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `is_route` tinyint(1) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  FULLTEXT KEY `page` (`page`),
  FULLTEXT KEY `content` (`content`),
  FULLTEXT KEY `keywords` (`keywords`)
) ENGINE=MyISAM AUTO_INCREMENT=664 DEFAULT CHARSET=utf8;



CREATE TABLE `alpha_languages` (
  `id` int(3) unsigned NOT NULL AUTO_INCREMENT,
  `lang_code` varchar(2) NOT NULL,
  `language` varchar(25) NOT NULL,
  `sort_order` int(3) unsigned NOT NULL,
  `is_default` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `is_protected` tinyint(1) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  FULLTEXT KEY `lang_code` (`lang_code`),
  FULLTEXT KEY `language` (`language`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;



CREATE TABLE `alpha_menu` (
  `id` int(3) unsigned NOT NULL AUTO_INCREMENT,
  `caller` varchar(255) NOT NULL,
  `parent_menu_id` int(3) unsigned NOT NULL,
  `menu_name` varchar(255) NOT NULL,
  `menu_link` varchar(255) DEFAULT NULL,
  `lang_id` int(3) unsigned NOT NULL,
  `sort_order` int(3) unsigned NOT NULL,
  `is_protected` tinyint(1) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  FULLTEXT KEY `caller` (`caller`),
  FULLTEXT KEY `menu_name` (`menu_name`),
  FULLTEXT KEY `menu_link` (`menu_link`)
) ENGINE=MyISAM AUTO_INCREMENT=152 DEFAULT CHARSET=utf8;



CREATE TABLE `alpha_users` (
  `id` int(3) unsigned NOT NULL AUTO_INCREMENT,
  `username` varchar(16) NOT NULL,
  `email` varchar(100) NOT NULL,
  `password` varchar(32) NOT NULL,
  `type` tinyint(1) unsigned NOT NULL,
  PRIMARY KEY (`id`),
  FULLTEXT KEY `email` (`email`),
  FULLTEXT KEY `username` (`username`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;



CREATE TABLE `alpha_logs` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `entity` varchar(18) NOT NULL,
  `error_code` varchar(5) DEFAULT NULL,
  `message` text NOT NULL,
  `file` varchar(255) DEFAULT NULL,
  `line` varchar(10) DEFAULT NULL,
  `date_time` datetime NOT NULL,
  PRIMARY KEY (`id`),
  FULLTEXT KEY `message` (`message`),
  FULLTEXT KEY `file` (`file`),
  FULLTEXT KEY `error_code` (`error_code`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;
